<template>
  <v-app-bar
    app
    class="indigo"
    flat
  >
    <v-container class="py-0 fill-height">
      <v-btn
        v-for="link in links"
        :key="link.route"
        :to="link.route"
        color="black"
        text
      >
        {{ link.title }}
      </v-btn>

      <v-spacer />

      <v-responsive max-width="260">
        <Search
          :search-value="inputData"
          @submitInput="search"
          @changeInput="inputData = $event"
        />
      </v-responsive>
    </v-container>
    <v-menu offset-y>
      <template v-slot:activator="{ on, attrs }">
        <v-badge
          bordered
          bottom
          color="deep-purple accent-4"
          offset-x="10"
          offset-y="10"
          :left="true"
          content="5"
        >
          <v-avatar
            size="40"
            v-bind="attrs"
            v-on="on"
          >
            <v-img
              src="data:image/jpeg;base64,"
            />
          </v-avatar>
        </v-badge>
      </template>
      <v-list>
        <v-list-item
          v-for="(item, index) in items"
          :key="index"
          :to="item.route"
        >
          <v-list-item-title>{{ item.title }}</v-list-item-title>
        </v-list-item>
        <v-sheet class="pa-5">
          <v-switch
            v-model="$vuetify.theme.dark"
            inset
            :label="`Dark mode`"
            @change="changeDarkMode"
          />
        </v-sheet>
      </v-list>
    </v-menu>
  </v-app-bar>
</template>

<script>
import Search from "./Search";
import {mapGetters} from 'vuex';
export default {
  name: "AppBar",
  components: {Search},
  data: () => ({
    inputData:'',
    links: [
      {title: 'Products', route: '/products'},
      {title: 'Contacts', route: '/contacts'},
      {title: 'About', route: '/about'},
    ],
    items: [
      {title: 'Account', route: '/account'},
      {title: 'Cart', route: '/cart'},
      {title: 'Settings', route: '/settings'},
      {title: 'Log Out'},
    ],
  }),
  computed:{
   ...mapGetters({
    isDarkModeEnabled: 'settings/getIsDarkModeEnabled'
  }),
  link: function (){
     return this.$route.path
  }
  },
  watch: {
    isDarkModeEnabled: {
      handler() {
        this.$vuetify.theme.dark = this.isDarkModeEnabled
      },
      immediate: true
    },
    link: function () {
      if (this.link !== '/products') {
        this.inputData = ''
      }
    },
  },
  methods: {
    changeDarkMode() {
      this.$store.commit('settings/mutateIsDarkModeEnabled', !this.isDarkModeEnabled);
    },
    search() {
      if (this.inputData !== '' && this.inputData !== this.$route.query.search) {
        this.searchValue = this.inputData
        this.$router.push({
          path: 'products',
          query:{
            link:`/ru/search/?query=${this.inputData}`
          }
        })
      }
    }
  }
}
</script>

